// Load font dynamically
function loadFont(fontKey) {
  const f = fontOptions[fontKey];
  if (!f || !f.url) return;
  const linkId = 'font-' + fontKey;
  if (!document.getElementById(linkId)) {
    const link = document.createElement('link');
    link.id = linkId;
    link.rel = 'stylesheet';
    link.href = f.url;
    document.head.appendChild(link);
  }
}

